s];    tips.numberOfLines = 0;    tips.lineBreakMode = NSLineBreakByWordWrapping;    tips.font = [UIFont systemFontOfSize:10];    tips.textColor = kLightTitleColor;    tips.font = [UIFont systemFontOfSize:18];    tips.textAlignment = NSTextAlignmentLeft;    [view addSubview:tips];        if ([self tips]) {        return view;    }        return  nil;}#pragma mark - talbeView delegate-(NSInteger)tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section{    return self.dataList.count;}-(UITableViewCell *)tableView:(UITableView *)tableView cellForRowAtIndexPath:(NSIndexPath *)indexPath{    UITableViewCell *cell = [tableView dequeueReusableCellWithIdentifier:@"reuseID"];        cell.selectionStyle = UITableViewCellSelectionStyleNone;        NSDictionary *data = self.dataList[indexPath.row];        cell.imageView.image = [UIImage imageNamed:data[@"image"]];        cell.textLabel.text = NSLocalizedString(data[@"title"], nil);    cell.textLabel.textColor = kLightTitleColor;        cell.accessoryType = UITableViewCellAccessoryDisclosureIndicator;    cell.backgroundColor = [UIColor whiteColor];        return cell;}-(CGFloat)tableView:(UITableView *)tableView heightForRowAtIndexPath:(NSIndexPath *)indexPath{    return  kCellHeigh;}- (NSInteger)numberOfSectionsInTableView:(UITableView *)tableView {        return 1;}@end