systemFontOfSize:12];    }    else if (UIScreen.mainScreen.bounds.size.width == 414){        lab.font = [UIFont systemFontOfSize:14];    }    lab.textColor = kLightTitleColor;        [self addSubview:lab];}- (instancetype)initWithFrame:(CGRect)frame{    self = [super initWithFrame:frame];    if (self) {                self.backgroundColor = [UIColor whiteColor];                UIButton *midBtn = [self buttonWithImage:                            [UIImage imageNamed:@"btn-im-home-nor"]                                   selectedImage:[UIImage imageNamed:@"btn-im-home-sel"] size:CGSizeMake(kButtonWidth, kButtonWidth)                                          center:CGPointMake(CGRectGetWidth(self.frame)/2.0f, CGRectGetHeight(self.frame)/2.5f)];                [self addSubview:midBtn];                [self viewWithButton:midBtn Title:NSLocalizedString(@"Stay", nil)];                UIButton *leftBtn = [self buttonWithImage:                             [UIImage imageNamed:@"btn-goodbye-nor"]                                    selectedImage:[UIImage imageNamed:@"btn-goodbye-sel"]                                             size:CGSizeMake(kButtonWidth, kButtonWidth)                                           center:CGPointMake(CGRectGetMinX(midBtn.frame)/2.0f, midBtn.center.y)];        [self addSubview:leftBtn];        [self viewWithButton:leftBtn Title:NSLocalizedString(@"Away", @"")];                UIButton *rightBtn = [self buttonWithImage:                              [UIImage imageNamed:@"btn-all-clear-nor"]                                     selectedImage:[UIImage imageNamed:@"btn-all-clear-sel"]                                              size:CGSizeMake(kButtonWidth, kButtonWidth)                                            center:CGPointMake((CGRectGetMaxX(self.frame)-CGRectGetMaxX(midBtn.frame))/2.0f+CGRectGetMaxX(midBtn.frame), midBtn.center.y)];        [self addSubview:rightBtn];        [self viewWithButton:rightBtn Title:NSLocalizedString(@"Disarm", @"")];                        leftBtn.tag = kLeftButton;        midBtn.tag = kMiddleButotn;        rightBtn.tag = kRightButton;            }    return self;}@end