straintMaker *make) {        __strong __typeof(wSelf)sSelf = wSelf;        make.left.mas_equalTo(_control.mas_right).offset(20);        make.centerY.mas_equalTo(_control.mas_centerY);        make.height.mas_equalTo(_control.mas_height).multipliedBy(0.7);        make.width.mas_equalTo(sSelf.contentView.mas_width).multipliedBy(0.5);    }];        [_accessBtn mas_makeConstraints:^(MASConstraintMaker *make) {        make.right.mas_equalTo(-20);        make.width.mas_equalTo(40);        make.height.mas_equalTo(40);        make.centerY.mas_equalTo(_control.mas_centerY);    }];        [self layoutIfNeeded];        _control.layer.cornerRadius = CGRectGetWidth(_control.frame) / 2.0f;    _control.clipsToBounds = YES;        _accessBtn.layer.cornerRadius = 20;    _accessBtn.clipsToBounds = YES;}-(void )setImage:(NSString *)img{    [_control setBackgroundImage:[UIImage imageNamed:img] forState:UIControlStateNormal];}-(void )setImageWithData:(UIImage *)img{    [_control setBackgroundImage:img forState:UIControlStateNormal];}-(void)setText:(NSString *)text{    _descLabel.text = text;}-(void)setSeleted:(BOOL)seleted{#if 0    if (seleted) {        _accessBtn.backgroundColor = kMainGreenColor;    }else    {        _accessBtn.backgroundColor = [UIColor lightGrayColor];    }#endif        _selected = seleted;    _accessBtn.selected = seleted;}-(BOOL)getSeleted{    return _selected;}-(void)addBtnClick:(UIButton *)btn{    [_delegate haveSeletedCell:self];}- (void)setSelected:(BOOL)selected animated:(BOOL)animated {    [super setSelected:selected animated:animated];        // Configure the view for the selected state}@end