#import "settingsViewController.h"#import "deviceViewController.h"#include "fileOperation.h"#import "simulatorOperation.h"@interface settingsViewController ()@end@implementation settingsViewController-(void )viewDidLoad{    [super viewDidLoad];        self.navigationItem.leftBarButtonItem = [[UIBarButtonItem alloc]initWithTitle:NSLocalizedString(@"Settings", nil) style:UIBarButtonItemStylePlain target:self action:nil];}-(void)createModels{    self.dataList = [[NSMutableArray alloc] initWithArray:[[fileOperation sharedOperation] getCellInfoWithCellName:@"settingCellInfo"]];}-(void)tableView:(UITableView *)tableView didSelectRowAtIndexPath:(NSIndexPath *)indexPath{    self.hidesBottomBarWhenPushed = YES;    // @"setRegionController", 暂时取消    NSArray *viewControllers = @[@"chooseHomeViewController",@"ActivityFeedViewController",@"configureViewController",@"deviceViewController",@"roomListViewController",@"sceneEditViewController",@"memberViewController",@"supportViewController"];        if (indexPath.row == 3) {        //deviceViewController 有自己的实例化方法        NSArray *devices = [[fileOperation sharedOperation] getDevices];        deviceViewController *device = [[deviceViewController alloc] initWithDevices:devices isSetting:YES];        [self.navigationController pushViewController:device animated:YES];       }else    {        [self.navigationController pushViewController:[[NSClassFromString(viewControllers[indexPath.row]) alloc]init] animated:YES];    }        self.hidesBottomBarWhenPushed = NO;}@end